# test_full_sequence.py

import pytest
from ReadGenbank import ReadGenbank
import json

# Test of sample record from NCBI:  https://www.ncbi.nlm.nih.gov/genbank/samplerecord/
def test_full_sequence_basic():
    reader = ReadGenbank()
    result_json = reader.run("SCU49845.gb", ["full_sequence"], [0, 30])
    result = json.loads(result_json)
    expected_sequence = "GATCCTCCATATACAACGGTATCTCCACCT"
    assert result["full_sequence"] == expected_sequence

def test_full_sequence_entire():
    reader = ReadGenbank()
    result_json = reader.run("SCU49845.gb", ["full_sequence"])
    result = json.loads(result_json)
    expected_sequence = ""
    assert result["full_sequence"] == expected_sequence

def test_full_sequence_unspecified_range():
    reader = ReadGenbank()
    result_json = reader.run("SCU49845.gb", ["full_sequence"], None)
    result = json.loads(result_json)
    expected_sequence = ""
    assert result["full_sequence"] == expected_sequence

def test_full_sequence_ape_file():
    reader = ReadGenbank()
    result_json = reader.run("p20N31.ape", ["full_sequence"])
    result = json.loads(result_json)
    expected_sequence = ""
    assert result["full_sequence"] == expected_sequence

def test_full_sequence_benchling_file():
    reader = ReadGenbank()
    result_json = reader.run("pbr322_egfr.gb", ["full_sequence"])
    result = json.loads(result_json)
    expected_sequence = ""
    assert result["full_sequence"] == expected_sequence

def test_full_sequence_range_exceeds_length():
    reader = ReadGenbank()
    with pytest.raises(ValueError) as exc_info:
        reader.run("SCU49845.gb", ["full_sequence"], [0, 10000])
    assert "Range exceeds the length of sequence" in str(exc_info.value)


def test_full_sequence_zero_length_range():
    reader = ReadGenbank()
    result_json = reader.run("SCU49845.gb", ["full_sequence"], [100, 100])
    result = json.loads(result_json)
    assert result["full_sequence"] == ""
   
